    private void confirmUButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_confirmUButtonActionPerformed
        String user = usernameTxt.getText();
        String pass = passwordTxt.getText();
        
        UserData.logins[UserData.userSize] = new Users(user,pass,0,
                UserData.userSize-1);
        
        UserData.userSize++;

        
        dispose();
                
    }//GEN-LAST:event_confirmUButtonActionPerformed

